<script>
    document.addEventListener("DOMContentLoaded", function() {
        const button = document.getElementById("menu-button");
        const menu = document.getElementById("menu");

        button.addEventListener("click", function() {
          if (menu.classList.contains("hidden")) {
            menu.classList.remove("hidden");                    // Show the menu
            void menu.offsetWidth;                              // Trigger reflow to make sure the removal of 'hidden' is rendered
            menu.classList.remove("opacity-0");                 // Start the fade-in animation
            menu.classList.add("opacity-100");
          } else {
            menu.classList.remove("opacity-100");               // Start the fade-out animation
            menu.classList.add("opacity-0");
            menu.addEventListener("transitionend", function() { // When the animation is done, add the 'hidden' class back
              if (menu.classList.contains("opacity-0")) {
                menu.classList.add("hidden");
              }
            }, { once: true });
          }
        });
      });
</script>
